class DestinationsFragment : Fragment() {

    private lateinit var destinationsRecyclerView: RecyclerView
    private lateinit var mountainsRecyclerView: RecyclerView
    private lateinit var templesRecyclerView: RecyclerView
    private lateinit var culinaryRecyclerView: RecyclerView
    private val destinationAdapter = DestinationAdapter(DummyData.destinations) { destination ->
        handleDestinationClick(destination)
    }

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_destinations, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        destinationsRecyclerView = view.findViewById(R.id.destinationsRecyclerView)
        mountainsRecyclerView = view.findViewById(R.id.mountainsRecyclerView)
        templesRecyclerView = view.findViewById(R.id.templesRecyclerView)
        culinaryRecyclerView = view.findViewById(R.id.culinaryRecyclerView)

        setupDestinations()
        setupMountains()
        setupTemples()
        setupCulinary()

        // Auto-scroll ke kategori jika ada argument
        val sharedPref = requireActivity().getSharedPreferences("tour_guide_prefs", Context.MODE_PRIVATE)
        val scrollToCategory = sharedPref.getString("scrollToCategory", null)
        if (scrollToCategory != null) {
            val scrollView = view.findViewById<NestedScrollView>(R.id.nestedScrollView)
            val anchorId = when (scrollToCategory) {
                "gunung" -> R.id.anchorGunung
                "candi" -> R.id.anchorCandi
                "kuliner" -> R.id.anchorKuliner
                else -> null
            }
            anchorId?.let {
                val anchorView = view.findViewById<View>(it)
                scrollView.post {
                    scrollView.smoothScrollTo(0, anchorView.top)
                }
            }
            sharedPref.edit().remove("scrollToCategory").apply()
        }
    }

